package user.zc.models.base;import lombok.Data;import user.zc.utils.BaseEntity;import java.util.ArrayList;import java.util.List;//表名称 permission@Datapublic class Permission extends BaseEntity {	private static final long serialVersionUID = 1L;	public Permission() {		super();	}	    private String name;    private String alias;    private String url;    private String parentId;    private Integer type;    private String sort;    private Integer depth;    private Integer platform;//1 PC  2 H5    private String description;    private Boolean status;    private List<Permission> menuList;    public void addMenu(Permission permission){        if(menuList==null){            menuList=new ArrayList<Permission>();        }        menuList.add(permission);    }    public Permission getLastMenuByDepth(int depth){//获取某层级的最后一个Menu        Permission currentPermission = this;        while(depth-->1){            currentPermission  = currentPermission.getMenuList().get(currentPermission.getMenuList().size()-1);        }        return currentPermission;    }}